import React, { useEffect, useState } from 'react'
import { editApplication } from '../services/jobApplicationService';

const EditModal = ({ application }) => {
    // Initializing state with the existing data
    const [data, setData] = useState({
        company: '',
        role: '',
        location: '',
        pay_range: '',
        source: '',
        date: '',
        call: '',
        progress: ''
    });

    useEffect(() => {
        if (application) {
            setData({
                company: application.company,
                role: application.role,
                location: application.location,
                pay_range: application.pay_range || '',
                source: application.source,
                date: application.date,
                call: application.call || '',
                progress: application.progress || ''
            });
        }
    }, [application]);

    const handleChange = (e) => {
        const { name, value } = e.target;

        setData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    }

    const handleSumbit = async (e) => {
        e.preventDefault();

        try {
            const [ body ] = { data };
            const updatedApplication = editApplication(data.id, body);
            setData(updatedApplication);
            window.location = "/";
        }
        catch (error) {
            console.log("Failed to update - frontend error: ", error);
        }
    }

    return (
        <>
            <button
                type="button"class="btn btn-info"
                data-toggle="modal"
                data-target={`#edit-id${application.id}`}
            >
                    Edit
            </button>

            <div class="modal" id={`edit-id${application.id}`}>
                <div class="modal-dialog">
                    <div class="modal-content">

                        <div class="modal-header">
                            <h4 class="modal-title">Edit Application</h4>
                            <button
                                type="button"
                                class="close"
                                data-dismiss="modal" 
                                onClick={() => setData({
                                    company: application.company,
                                    role: application.role,
                                    location: application.location,
                                    pay_range: application.pay_range || '',
                                    source: application.source,
                                    date: application.date,
                                    call: application.call || '',
                                    progress: application.progress || ''
                                })}
                            >
                                    &times;
                            </button>
                        </div>

                        <div class="modal-body">
                            <form onChange={handleSumbit}>
                                <div className='form-group'>
                                    <label>Company</label>
                                    <input
                                        type='text'
                                        className='form-control'
                                        name='company'
                                        value={data.company}
                                        onChange={handleChange}
                                    />
                                </div>
                                <div className='form-group'>
                                    <label>Role</label>
                                    <input
                                        type='text'
                                        className='form-control'
                                        name='role'
                                        value={data.role}
                                        onChange={handleChange}
                                    />
                                </div>
                                <div className='form-group'>
                                    <label>Location</label>
                                    <input
                                        type='text'
                                        className='form-control'
                                        name='location'
                                        value={data.location}
                                        onChange={handleChange}
                                    />
                                </div>
                                <div className='form-group'>
                                    <label>Pay Range</label>
                                    <input
                                        type='text'
                                        className='form-control'
                                        name='pay_range'
                                        value={data.pay_range}
                                        onChange={handleChange}
                                    />
                                </div>
                                <div className='form-group'>
                                    <label>Source</label>
                                    <input
                                        type='text'
                                        className='form-control'
                                        name='source'
                                        value={data.source}
                                        onChange={handleChange}
                                    />
                                </div>
                                <div className='form-group'>
                                    <label>Date</label>
                                    <input
                                        type='text'
                                        className='form-control'
                                        name='date'
                                        value={data.date}
                                        onChange={handleChange}
                                    />
                                </div>
                                <div className='form-group'>
                                    <label>CompaCallny</label>
                                    <input
                                        type='text'
                                        className='form-control'
                                        name='call'
                                        value={data.call}
                                        onChange={handleChange}
                                    />
                                </div>
                                <div className='form-group'>
                                    <label>Progress</label>
                                    <input
                                        type='text'
                                        className='form-control'
                                        name='progess'
                                        value={data.progress}
                                        onChange={handleChange}
                                    />
                                </div>
                            </form>
                        </div>

                        <div class="modal-footer">
                            <button
                                type="button"
                                class="btn btn-info"
                                data-dismiss="modal" 
                                onClick={handleSumbit}
                            >
                                    Edit
                            </button>
                            <button
                                type="button"
                                class="btn btn-danger"
                                data-dismiss="modal" 
                                onClick={() => setData({
                                    company: application.company,
                                    role: application.role,
                                    location: application.location,
                                    pay_range: application.pay_range || '',
                                    source: application.source,
                                    date: application.date,
                                    call: application.call || '',
                                    progress: application.progress || ''
                                })}
                            >
                                    Dismiss
                            </button>
                        </div>

                    </div>
                </div>
            </div>
        </>
    );
};

export default EditModal
