import React, { useEffect, useState, Suspense, lazy } from 'react'
import { getAllApplications } from '../services/jobApplicationService';
import { FixedSizeList as List } from 'react-window';
// import EditModal from './EditModal';

const EditModal = lazy(() => import('./EditModal'));

const JobApplicationTable = () => {
    const [applications, setApplications] = useState([]);
    const [selectedApplication, setSelectedApplication] = useState(null);
    // eslint-disable-next-line
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        const fetchApplications = async () => {
            try {
                const data = await getAllApplications();
                setApplications(data);
            }
            catch (error) {
                console.log("Error fetching applications from React Service layer..", error);
            }
        };

        fetchApplications();
    }, []);

    const handleDelete = async (id) => {
        console.log("Delete feature function");
    };

    function handleEditClick(application) {
        setSelectedApplication(application);
        setIsModalOpen(true);

        console.log("Inside HANDLE EDIT");
    }

    const handleModalClose = (application) => {
        setSelectedApplication(null);
        setIsModalOpen(false);
    }

    // Render each row
    const Row = ({ index, style }) => {
        const application = applications[index];
        return (
            <div style={style} className="row-item">
                <table class="table table-dark table-hover text-center">
                    <thead className='thead-light'>
                        <tr>
                            <th>Company</th>
                            <th>Role</th>
                            <th>Location</th>
                            <th>Pay Range</th>
                            <th>Source</th>
                            <th>Date</th>
                            <th>Call</th>
                            <th>Progress</th>
                            <th>Edit</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {applications.map(application => (
                            <tr key={application.id}>
                                <td>{application.company}</td>
                                <td>{application.role}</td>
                                <td>{application.location}</td>
                                <td>{application.pay_range}</td>
                                <td>{application.source}</td>
                                <td>{application.date}</td>
                                <td>{application.call}</td>
                                <td>{application.progress}</td>
                                <td>
                                    <EditModal application={application} onClick={handleEditClick} onClose={{handleModalClose}} />
                                    {/* <button
                                        className='btn btn-primary'
                                        onClick={() => handleEditClick(application)}
                                    >
                                        Edit
                                    </button> */}
                                </td>
                                <td><button className='btn btn-danger' onClick={() => handleDelete(application.id)}>Delete</button></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        );
    };

    return (
        <>
            <div>
                {/* <h1 className='text-center mt-5'>ListTodos</h1> */}
                <div className='d-flex mt-5'>
                    {/* Virtualized List */}
                    {/* <List
                        height={400} // Visible height of the list container
                        itemCount={applications.length} // Total items in the list
                        itemSize={50} // Height of each item
                        width="100%" // Width of the list container
                    >
                        {Row}
                    </List> */}

                    <table class="table table-dark table-hover text-center">
                        <thead className='thead-light'>
                            <tr>
                                <th>Company</th>
                                <th>Role</th>
                                <th>Location</th>
                                <th>Pay Range</th>
                                <th>Source</th>
                                <th>Date</th>
                                <th>Call</th>
                                <th>Progress</th>
                                <th>Edit</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {applications.map(application => (
                                <tr key={application.id}>
                                    <td>{application.company}</td>
                                    <td>{application.role}</td>
                                    <td>{application.location}</td>
                                    <td>{application.pay_range}</td>
                                    <td>{application.source}</td>
                                    <td>{application.date}</td>
                                    <td>{application.call}</td>
                                    <td>{application.progress}</td>
                                    <td>
                                        <EditModal application={application} onClick={handleEditClick} onClose={{handleModalClose}} />
                                        {/* <button
                                            className='btn btn-primary'
                                            onClick={() => handleEditClick(application)}
                                        >
                                            Edit
                                        </button> */}
                                    </td>
                                    <td><button className='btn btn-danger' onClick={() => handleDelete(application.id)}>Delete</button></td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                {/* Lazy-loaded Modal */}
                {isModalOpen && selectedApplication && (
                    <Suspense fallback={<div>Loading...</div>}>
                        <EditModal
                            application={selectedApplication}
                            onClose={handleModalClose}
                        />
                    </Suspense>
                )}

                {/* {(isModalOpen && selectedApplication && (
                    <EditModal
                        application={selectedApplication}
                        onClose={handleModalClose}
                    />
                ))} */}
            </div>
        </>
    )
}

export default JobApplicationTable
