import React, { useEffect, useState } from 'react'
import { getAllApplications } from '../services/jobApplicationService';

const JobApplicationTable = () => {
    const [applications, setApplications] = useState([]);

    useEffect(() => {
        const fetchApplications = async () => {
            try {
                const data = await getAllApplications();
                setApplications(data);
            }
            catch (error) {
                console.log("Error fetching applications from React Service layer..", error);
            }
        };

        fetchApplications();
    }, []);

    return (
        <>
            <div>
                {/* <h1 className='text-center mt-5'>ListTodos</h1> */}

                <div className='d-flex mt-5'>
                    <table class="table table-hover text-center">
                        <thead className='thead-light'>
                            <tr>
                                <th>Company</th>
                                <th>Role</th>
                                <th>Location</th>
                                <th>Pay Range</th>
                                <th>Source</th>
                                <th>Call</th>
                                <th>Progress</th>
                                <th>Edit</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {todos.map(todo => (
                                <tr key={todo.todo_id}>
                                    <td>{todo.todo_id}</td>
                                    <td>{todo.description}</td>
                                    <td><EditTodo todo = {todo} /></td>
                                    <td><DeleteTodo todo = {todo} /></td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    )
}

export default JobApplicationTable
