import React, { useEffect, useState } from 'react'
import { getAllApplications } from '../services/jobApplicationService';
import EditModal from './EditModal';

const JobApplicationTable = () => {
    const [applications, setApplications] = useState([]);
    const [selectedApplication, setSelectedApplication] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        const fetchApplications = async () => {
            try {
                const data = await getAllApplications();
                setApplications(data);
            }
            catch (error) {
                console.log("Error fetching applications from React Service layer..", error);
            }
        };

        fetchApplications();
    }, []);

    const handleDelete = async (id) => {
        console.log("Delete feature function");
    };

    function handleEditClick(application) {
        setSelectedApplication(application);
    }

    const handleModalClose = () => {
        setSelectedApplication = null;
        setIsModalOpen = false;
    }

    return (
        <>
            <div>
                {/* <h1 className='text-center mt-5'>ListTodos</h1> */}
                <div className='d-flex mt-5'>
                    <table class="table table-dark table-hover text-center">
                        <thead className='thead-light'>
                            <tr>
                                <th>Company</th>
                                <th>Role</th>
                                <th>Location</th>
                                <th>Pay Range</th>
                                <th>Source</th>
                                <th>Date</th>
                                <th>Call</th>
                                <th>Progress</th>
                                <th>Edit</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {applications.map(application => (
                                <tr key={application.id}>
                                    <td>{application.company}</td>
                                    <td>{application.role}</td>
                                    <td>{application.location}</td>
                                    <td>{application.pay_range}</td>
                                    <td>{application.source}</td>
                                    <td>{application.date}</td>
                                    <td>{application.call}</td>
                                    <td>{application.progress}</td>
                                    {/* <td><EditModal application = { application } /></td> */}
                                    <td>
                                        <button
                                            className='btn btn-secondary'
                                            data-toggle="modal"
                                            data-target="#editModal"
                                            onClick={() => handleEditClick(application)}
                                        >
                                            Edit
                                        </button>
                                    </td>
                                    <td><button className='btn btn-danger' onClick={() => handleDelete(application.id)}>Delete</button></td>
                                </tr>
                            ))}
                        </tbody>
                    </table>

                    (selectedApplication && (
                        <EditModal application={selectedApplication} />
                    ))
                </div>
            </div>
        </>
    )
}

export default JobApplicationTable
