import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import { addApplication } from "../services/jobApplicationService";

const AddModal = () => {
  const [show, setShow] = useState(false);
  const [data, setData] = useState({
    company: "",
    role: "",
    location: "",
    pay_range: "",
    source: "",
    date: "",
    call: "",
    progress: "",
  });

  const handleClose = () => {
    setShow(false);
    resetForm();
  };
  //   const handleShow = () => setShow(true);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  const onSubmitForm = async (e) => {
    e.preventDefault();
    try {
      const body = { data };
      const newApplication = await addApplication(body);
      handleClose();
      window.location = "/";
    } catch (err) {
      console.log(err.message);
    }
  };

  const resetForm = () => {
    setData({
      company: "",
      role: "",
      location: "",
      pay_range: "",
      source: "",
      date: "",
      call: "",
      progress: "",
    });
  };

  return (
    <>
      <button
        type="button"
        class="btn btn-success"
        data-toggle="modal"
        data-target="#newApplication"
        onClick={() => setDescription("")}
      >
        New
      </button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add Application</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Control
                type="text"
                name="company"
                value={data.company}
                onChange={handleInputChange}
                placeholder="Enter company name"
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Control
                type="text"
                name="role"
                value={data.role}
                onChange={handleInputChange}
                placeholder="Enter role"
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Control
                type="text"
                name="location"
                value={data.location}
                onChange={handleInputChange}
                placeholder="Enter location"
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Control
                type="text"
                name="pay_range"
                value={data.pay_range}
                onChange={handleInputChange}
                placeholder="Enter pay range"
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Control
                type="text"
                name="source"
                value={data.source}
                onChange={handleInputChange}
                placeholder="Enter source"
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Control
                type="date"
                name="date"
                value={data.date}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Control
                type="text"
                name="call"
                value={data.call}
                onChange={handleInputChange}
                placeholder="Enter call details"
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Control
                type="text"
                name="progress"
                value={data.progress}
                onChange={handleInputChange}
                placeholder="Enter progress"
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="success" onClick={onSubmitForm}>
            Add
          </Button>
          <Button variant="danger" onClick={handleClose}>
            Dismiss
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default AddModal;
